.card {
 font-family: Liberation Sans;
 font-size: 20px;
 text-align: center;
 color: black;
 background-color: white;
}

.meaning {
font-size: 15px;
font-style: italic;
}

.typeGood {
background-color: transparent;
}

.typeBad {
background-color: transparent;
color: red;
}

.typeMissed {
background-color: transparent;
}

#typeans {
color: #303c41;
font-family: Liberation Sans;
background-color: #0000;
border-style: solid;
border-color: transparent transparent #303c41 transparent;
border: 0 0 3px 0;
text-align: center;
}

#typeans:focus {
border-color: transparent transparent red transparent;
outline: none;
}

